import React from 'react'
import {createStructuredSelector} from 'reselect';
import {selectCurrentUser} from './Redux/UserReducer/user.selector';
import './App.css';
import {Route,Redirect} from 'react-router-dom';
import { Homepage } from './pages/homepage/homepage.component';
import ShopPage from './pages/shop-page/shoppage.component';
import Header from './components/Header/header.component';
import SignIn from './components/SignIn/signin.component';
import {auth} from './FireBase/firebase.utils';
import {createProfile, addCollectionsAndDocs} from './FireBase/firebase.utils';
import LogIn from './pages/LogIn/login.component';
import {setCurrentUser} from './Redux/UserReducer/user.actions';
import {connect} from 'react-redux';
import Checkout from './pages/Checkout/checkout.component';//
import {selectCollectionsForPreview} from './Redux/Shop/shop.selector';
class App extends React.Component{
  constructor(){
    super();

  }
 unsubscribeFromAuth = null;

  componentDidMount(){
    const {setCurrentUser,collectionsArray} = this.props;
    this.unsubscribeFromAuth=auth.onAuthStateChanged(async userAuth => {
      if(userAuth){
        console.log(userAuth);
        const userRef= await createProfile(userAuth);
        userRef.onSnapshot(snap => {
          setCurrentUser({
            id:snap.id,
            ...snap.data()
          })
        })
      }
          setCurrentUser(userAuth);
       //  addCollectionsAndDocs('collections',collectionsArray.map(({title,items}) => ({title,items})));
    })

  }

  componentWillUnmount(){
    this.unsubscribeFromAuth();
  }

  render(){
    return (
      <div className="App">
        <Header></Header>
        <Route exact={true} path='/' component={Homepage}></Route>
        <Route  path='/shop' component={ShopPage} />
        <Route exact path='/checkout' component={Checkout}></Route>
        <Route exact path='/signin' render={()=> this.props.currentUser?
      ( <Redirect to='/'></Redirect> ) : <LogIn></LogIn>  
      } />

      </div>
    );
  }
}
const mapToStateProps =  createStructuredSelector({
  currentUser:selectCurrentUser,
//  collectionsArray : selectCollectionsForPreview
});
const mapDispatchToProps = dispatch => ({
  setCurrentUser:user => dispatch(
    setCurrentUser(user)
  )
});
export default connect(mapToStateProps,mapDispatchToProps)(App);
