import React from 'react';
import CollectionOverview from '../../components/CollectionOverview/collectionoverview.component';
import {Route} from 'react-router-dom';
import CollectionPage from '../Collections/collection.component';
import {fetchCollStartAsync} from '../../Redux/Shop/shop.actions';
import {connect} from 'react-redux';
import {updateCollections} from '../../Redux/Shop/shop.actions';
import {selectCollFetch,selectCollLoaded} from '../../Redux/Shop/shop.selector';
import {createStructuredSelector} from 'reselect';
import withLoader from '../../components/Loader/withloader.component';

const CollectionOverviewSpinner = withLoader(CollectionOverview);
const CollectionPageSpinner = withLoader(CollectionPage);


class ShopPage extends React.Component {

   componentDidMount(){
     const {fetchCollStartAsync} = this.props;
     fetchCollStartAsync();
   }
      render(){
        const {match,isCollFetch,isCollLoaded}=this.props;
        
        return (
            <div className="shop-page">
              <Route exact path={`${match.path}`} render={(props) => <CollectionOverviewSpinner isLoading={isCollFetch} {...props} ></CollectionOverviewSpinner>}></Route>
              <Route  path={`${match.path}/:collId`} render={(props) => <CollectionPageSpinner isLoading={isCollLoaded} {...props}></CollectionPageSpinner>}></Route>
            </div>
        );
        }
}

const mapStateToProps = createStructuredSelector({
  isCollFetch : selectCollFetch,
  isCollLoaded: selectCollLoaded
});

const mapDispatchToProps = dispatch => ({
  fetchCollStartAsync : () => dispatch(fetchCollStartAsync())
});

export default connect(mapStateToProps,mapDispatchToProps)(ShopPage);
