import {cartActionTypes} from './cart.types';

const InitialState = {
    hidden:true
};

 const cartReducer = (state=InitialState,action) => {
     switch(action.type){
         case cartActionTypes.TOGGLE_CART_HIDDEN :
             return {
                 ...state,
                 hidden:!state.hidden
             };
        default: return state; 
     }
 }
 export default cartReducer;
