import React from 'react';
import './signup.styles.scss';
import {connect} from 'react-redux';

import FormInput from '../FormInput/forminput.component';
import DButton from '../DButton/dbutton.component';
import {signUpStart} from '../../Redux/UserReducer/user.actions';
class SignUp extends React.Component{
    constructor(){
        super();
        this.state = {
            displayName:'',
            email:'',
            password:'',
            confirmpassword:''
        };
    }
    handleChange = (event) => {
        const {name,value}=event.target;
        this.setState({[name]:value});
    }
    handleSubmit = async event => {
        event.preventDefault();
        const {displayName,email,password,confirmpassword}= this.state;
        if(password!==confirmpassword){
            return;
        }
       const {signUpStart}=this.props;
       signUpStart(email,password,displayName);

    }

    render(){
        return(
            <div className='sign-up'>
                <h2 className='title'>Sign Up for our Shop</h2>
                <form onSubmit={this.handleSubmit}>
                <FormInput name='displayName' value={this.state.displayName} label='Name' onChange={this.handleChange} type='text' required></FormInput>
                <FormInput name='email' value={this.state.email} label='Email Id' onChange={this.handleChange} type='email' required></FormInput>
                <FormInput name='password' value={this.state.password} label='Password' onChange={this.handleChange} type='password' required></FormInput>
                <FormInput name='confirmpassword' value={this.state.confirmpassword} label='Confirm Password' onChange={this.handleChange} type='password' required></FormInput>
               
               <DButton type='submit'>Sign Up</DButton>
                </form>
              
            </div>
        )
    }
}

const mapDispatchToProps = dispatch => ({
    signUpStart : (email,password,displayName) => dispatch(signUpStart({email,password,displayName}))
});
 export default connect(null,mapDispatchToProps)(SignUp);
