import React from 'react';
import {connect} from 'reselect';
import './signin.styles.scss';
import FormInput from '../FormInput/forminput.component';
import DButton from '../DButton/dbutton.component';

import {emailSignInStart,googleSignInStart} from '../../Redux/UserReducer/user.actions';
class SignIn extends React.Component{
    constructor(props){
        super(props);
        this.state={
            email:'',
            password:''
        };
    }
onnsubmit = async event => {
    event.preventDefault();
    const {email,password}=this.state;
    const {emailSignInStart} = this.props;
    emailSignInStart(email,password);
   
}

 handleechange = (event) => {
     const {value,name} = event.target;
     this.setState({[name]:value});
 } 

    render(){
        const {googleSignInStart}=this.props;
        return(
            <div className='sign-in'>
                <h1>Sign In to get an amazing shopping experience.</h1>
                <form onSubmit={this.onnsubmit}>
                    <FormInput name='email' type='email' label='E-Mail' handleChange={this.handleechange} value={this.state.email} required></FormInput> 
                    <FormInput name='password' type='password' label='Password' handleChange={this.handleechange} value={this.state.password} required></FormInput> 
                    <DButton type='submit'>Submit It</DButton>
                    <DButton type='button' onClick={googleSignInStart} google  >
                        {''} Sign In with Google
                    </DButton>
                </form>
            </div>
        )
    }
}

const mapDispatchToProps = dispatch => ({
    googleSignInStart : () => dispatch(googleSignInStart()),
    emailSignInStart : (email,password) => dispatch(emailSignInStart({email,password}))
});

export default connect(null,mapDispatchToProps)(SignIn);
