import React from 'react';
import CollectionItem from '../CollectionItem/collectionitem.component';
import {connect} from 'react-redux';
import {createStructuredSelector} from 'reselect';
import {selectCollectionsForPreview} from '../../Redux/Shop/shop.selector';
import {useState} from 'react';
import {fetchCollToStart} from '../../Redux/Shop/shop.actions';
import FormInput from '../FormInput/forminput.component';
const Search = ({collections}) => {
    const [search,setSearch] = useState('');
    const handlechange = (event) => {
        setSearch(event.target.value);
    }
    fetchCollToStart();
    return(
        <div>
        <form>
            <FormInput type='text' name='search' handleChange={handlechange} >
             </FormInput>
        </form>
        {
         search ?( 
         <React.Fragment>
                     <h2>Here are results</h2> 
                     <div>
                     {   collections.map((collection) => collection.items.filter( ({name}) => name.toLowerCase().includes(search.toLowerCase()) ).map( item =>(
                    
                       <CollectionItem key={item.id} item={item}>
                
                         </CollectionItem>
                           ) )
                          )
                         }
                     </div>
                     
         </React.Fragment>
            ) :
         <h3>Enter something</h3>
        }
        </div>
    )
    
}


const mapStateToProps = createStructuredSelector({
    collections : selectCollectionsForPreview
});

const mapDispatchToProps = dispatch => ({
    fetchCollToStart : () => dispatch(fetchCollToStart())
  });
export default connect(mapStateToProps,mapDispatchToProps)(Search);
