import React from 'react';
import './signin.styles.scss';
import FormInput from '../FormInput/forminput.component';
import DButton from '../DButton/dbutton.component';
import {auth, signInWithG} from '../../FireBase/firebase.utils';
class SignIn extends React.Component{
    constructor(props){
        super(props);
        this.state={
            email:'',
            password:''
        };
    }
onnsubmit = async event => {
    event.preventDefault();
    const {email,password}=this.state;
    try{
        await auth.signInWithEmailAndPassword(email,password);
        this.setState ({email:'',password:''});
    }
    catch(error){
        console.log("error");
    }
   
}

 handleechange = (event) => {
     const {value,name} = event.target;
     this.setState({[name]:value});
 } 

    render(){
        return(
            <div className='sign-in'>
                <h1>Sign In to get an amazing shopping experience.</h1>
                <form onSubmit={this.onnsubmit}>
                    <FormInput name='email' type='email' label='E-Mail' handleChange={this.handleechange} value={this.state.email} required></FormInput> 
                    <FormInput name='password' type='password' label='Password' handleChange={this.handleechange} value={this.state.password} required></FormInput> 
                    <DButton type='submit'>Submit It</DButton>
                    <DButton onClick={signInWithG} google>
                        {''} Sign In with Google
                    </DButton>
                </form>
            </div>
        )
    }
}

export default SignIn;
