    const onToken = token => {
        alert("DONE");
    }
    return(
        <StripeCheckout label='Pay Now' name='Shop' billingAddress shippingAddress 
        description={`PAY ${price}`} amount={price} token={onToken} panelLabel='Pay' stripeKey={publishkey}>

        </StripeCheckout>
    );
}

export default StripeButton;
