import React from 'react';
import CollectionOverview from '../../components/CollectionOverview/collectionoverview.component';
import {Route} from 'react-router-dom';
import CollectionPage from '../Collections/collection.component';
import {firestore,CollectionSnapToMap} from '../../FireBase/firebase.utils';
import {connect} from 'react-redux';
import {updateCollections} from '../../Redux/Shop/shop.actions';
import withLoader from '../../components/Loader/withloader.component';

const CollectionOverviewSpinner = withLoader(CollectionOverview);
const CollectionPageSpinner = withLoader(CollectionPage);


class ShopPage extends React.Component {
   unsubscribefromsnap = null;
   state={
     loading:true
   };
   componentDidMount(){
     const {updateCollections}=this.props;
     const collRef = firestore.collection('collections');
     collRef.get().then(snap => {
       const CollMap = CollectionSnapToMap(snap);
       updateCollections(CollMap);
       this.setState({loading:false});
     });
   }
      render(){
        const {match}=this.props;
        const {loading}=this.state;
        return (
            <div className="shop-page">
              <Route exact path={`${match.path}`} render={(props) => <CollectionOverviewSpinner isLoading={loading} {...props} ></CollectionOverviewSpinner>}></Route>
              <Route  path={`${match.path}/:collId`} render={(props) => <CollectionPageSpinner isLoading={loading} {...props}></CollectionPageSpinner>}></Route>
            </div>
        );
        }
}

const mapDispatchToProps = dispatch => ({
  updateCollections : collectionMap => dispatch(updateCollections(collectionMap))
});

export default connect(null,mapDispatchToProps)(ShopPage);
