import React from 'react';
import './signin.styles.scss';
import FormInput from '../FormInput/forminput.component';

class SignIn extends React.Component{
    constructor(props){
        super(props);
        this.state={
            email:'',
            password:''
        };
    }
submit = (event) => {
    event.preventDefault();
    this.setState ({email:'',password:''});
}

 change = (event) => {
     const {val,name} = event.target;
     this.setState({[name]:val});
 } 

    render(){
        return(
            <div className='sign-in'>
                <h1>Sign In to get an amazing shopping experience.</h1>
                <form onSubmit={this.submit}>
                    <FormInput name='email' type='email' label='E-Mail' onChange={this.change} required></FormInput> 
                    <FormInput name='password' type='password' label='Password' onChange={this.change} required></FormInput> 
                </form>
            </div>
        )
    }
}

export default SignIn;
